import React from 'react';
import { Navbar, NavbarBrand, Nav, NavItem, Button } from 'reactstrap';

const Toolbar = ({ toggle, toggleEditorView }) => {

    const label = toggleEditorView ? <span>Cancel</span> : <span><strong>+</strong> New Note</span>
    const btnColor = toggleEditorView ? 'warning' : 'info'

    return (
        <Navbar color="light" light className="border-bottom">
            <NavbarBrand>
                <strong>TakeNote</strong>
            </NavbarBrand>
            <Nav>
                <NavItem>
                    <Button color={btnColor} onClick={toggle}>
                        {label}
                    </Button>
                </NavItem>
            </Nav>
        </Navbar>
    )
}

export default Toolbar;
