import React from 'react';
import {
    Navbar,
    NavbarBrand,
    Nav,
    NavItem,
    Button
} from 'reactstrap';

const Toolbar = ({ toggle, toggleView }) => {

    const handleToggle = () => {
        toggle(!toggleView);
    }

    console.log(toggleView)
    const label = toggleView ? '<strong>+</strong> Create New Note' : 'Cancel'

    return (
        <Navbar color="light" light className="border-bottom">
            <NavbarBrand>
                <strong>TakeNote</strong>
            </NavbarBrand>
            <Nav>
                <NavItem>
                    <Button color="info" onClick={() => {
                        handleToggle()
                    }}>
                        {label}
                    </Button>
                </NavItem>
            </Nav>
        </Navbar>
    )
}

export default Toolbar;
